import React, { useState, useEffect } from "react";
import { ToastContainer, toast } from "react-toastify";
import "react-toastify/dist/ReactToastify.css";
// import { Card, CardContent } from "@/components/ui/card";
// import { Button } from "@/components/ui/button";

const FarmerDashboard = () => {
  const [profile, setProfile] = useState({
    name: "John Doe",
    email: "john@example.com",
  });
  const [marketRates, setMarketRates] = useState([]);
  const [transactions, setTransactions] = useState([]);

  useEffect(() => {
    fetchMarketRates();
    fetchTransactions();
  }, []);

  const fetchMarketRates = async () => {
    // Simulated API call
    setMarketRates([
      { crop: "Wheat", rate: "₹20/kg" },
      { crop: "Rice", rate: "₹30/kg" },
    ]);
  };

  const fetchTransactions = async () => {
    // Simulated API call
    setTransactions([
      { id: 1, crop: "Wheat", amount: "₹2000" },
      { id: 2, crop: "Rice", amount: "₹3000" },
    ]);
  };

  const handleLogout = () => {
    toast.success("Logged out successfully");
    // Implement logout logic here
  };

  const handleEditProfile = () => {
    toast.info("Profile editing coming soon");
  };

  const handleFeedback = () => {
    toast.success("Feedback submitted successfully");
  };

  return (
    <div className="p-4 flex flex-col h-screen bg-gray-100">
      {/* Profile Section */}
      <div className="flex justify-end">
        <Card className="p-4">
          <p>{profile.name}</p>
          <p className="text-sm text-gray-500">{profile.email}</p>
          <Button className="mt-2" onClick={handleEditProfile}>
            Edit Profile
          </Button>
          <Button className="mt-2 bg-red-500 text-white" onClick={handleLogout}>
            Logout
          </Button>
        </Card>
      </div>

      {/* Main Content */}
      <div className="flex mt-4 gap-4">
        {/* Market Rates */}
        <Card className="flex-1">
          <CardContent>
            <h2 className="text-xl font-bold mb-2">Market Rates</h2>
            {marketRates.map((rate, index) => (
              <p key={index}>
                {rate.crop}: {rate.rate}
              </p>
            ))}
          </CardContent>
        </Card>

        {/* Transactions */}
        <Card className="w-1/3">
          <CardContent>
            <h2 className="text-xl font-bold mb-2">Transaction History</h2>
            {transactions.map((txn) => (
              <p key={txn.id}>
                {txn.crop}: {txn.amount}
              </p>
            ))}
          </CardContent>
        </Card>
      </div>

      {/* Feedback Button */}
      <div className="mt-4">
        <Button className="bg-blue-500 text-white" onClick={handleFeedback}>
          Give Feedback
        </Button>
      </div>

      <ToastContainer />
    </div>
  );
};

export default FarmerDashboard;
