import React, { useState, useEffect } from "react";
import { ToastContainer, toast } from "react-toastify";
import "react-toastify/dist/ReactToastify.css";
import Dropdown from "react-bootstrap/Dropdown";
import "bootstrap/dist/css/bootstrap.min.css"; // Ensure Bootstrap is imported

const BuyerDashboard = () => {
  const [userName, setUserName] = useState("Nikhil Shinde"); // Example name

  // Function to get the first letter
  const getInitial = (name) => {
    return name ? name.charAt(0).toUpperCase() : "?";
  };

  return (
    <div className="container-fluid">
      <ToastContainer />

      {/* Header */}
      <div className="d-flex justify-content-between align-items-center bg-dark text-white p-3">
        <div className="logo">[Logo]</div>

        {/* Profile Dropdown */}
        <Dropdown align="end">
          <Dropdown.Toggle
            variant="link"
            id="dropdown-basic"
            className="p-0 border-0 text-white"
          >
            <div className="profile-circle">{getInitial(userName)}</div>
          </Dropdown.Toggle>

          <Dropdown.Menu>
            <Dropdown.Item href="#/edit-profile">Edit Profile</Dropdown.Item>
            <Dropdown.Item href="#/logout">Logout</Dropdown.Item>
          </Dropdown.Menu>
        </Dropdown>
      </div>

      {/* Rest of the dashboard content here... */}
    </div>
  );
};

export default BuyerDashboard;
