import React from "react";
import Navbar from "../components/navbar";

function HomePage() {
  return (
    <div className="min-h-screen flex flex-col">
      {/* Navbar */}
      <Navbar />

      {/* Main Content */}
      <div
        className="relative flex-1 bg-cover bg-center text-white flex items-center justify-center"
        style={{
          backgroundImage: "url('https://source.unsplash.com/1600x900/?farm,vegetables')",
        }}
      >
        {/* Overlay */}
        <div className="absolute inset-0 bg-black bg-opacity-60"></div>

        {/* Content */}
        <div className="relative z-10 text-center px-6">
          <h1 className="text-4xl md:text-6xl font-bold mb-4">
            Welcome to <span className="text-green-400">FARMER Welfare</span>
          </h1>
          <p className="text-lg md:text-2xl mb-8">
            Access market rates, feedback, and more to empower farmers.
          </p>

          {/* Market Rates Section */}
          <div className="bg-white bg-opacity-90 rounded-xl shadow-lg p-8 inline-block">
            <h2 className="text-4xl md:text-6xl font-bold text-green-700">
              Tomato Market Rate: â‚¹50/kg
            </h2>
          </div>
        </div>
      </div>
    </div>
  );
}

export default HomePage;
