import React, { useState } from "react";
import Navbar from "../components/Navbar"; // Assuming Navbar is imported from components
import { Link } from "react-router-dom";
import { ToastContainer, toast } from "react-toastify"; // Import Toastify
import "react-toastify/dist/ReactToastify.css"; // Import Toastify styles

const Shopkeeper = () => {
  const [profileData, setProfileData] = useState({
    name: "John Doe",
    email: "johndoe@example.com",
  });
  const [isEditing, setIsEditing] = useState(false);

  // Handle input change for editing profile
  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setProfileData({
      ...profileData,
      [name]: value,
    });
  };

  // Toggle between edit and save profile state
  const handleEditProfile = () => {
    if (isEditing) {
      toast.success("Profile updated successfully!");
      // Add logic to save the profile changes (e.g., update server or local storage)
    }
    setIsEditing(!isEditing);
  };

  const handleLogout = () => {
    // Handle logout logic (e.g., clearing sessionStorage or redirecting to login page)
    toast.success("Successfully logged out!");
  };

  return (
    <div className="container">
      <Navbar />
      <div className="row mt-4">
        {/* Left Section */}
        <div className="col-md-8">
          <div className="mb-4">
            <h3>Transaction History</h3>
            <ul className="list-group">
              <li className="list-group-item">
                <Link to="/transaction-history">View All Transactions</Link>
              </li>
            </ul>
          </div>

          <div className="mb-4">
            <h3>Farmer Details</h3>
            <ul className="list-group">
              <li className="list-group-item">
                <Link to="/farmer-details">View Farmer Details</Link>
              </li>
            </ul>
          </div>

          <div className="mb-4">
            <h3>Generate Bill</h3>
            <Link to="/generate-bill" className="btn btn-primary">
              Generate Bill
            </Link>
          </div>
        </div>

        {/* Right Section (Profile Section with Dropdown) */}
        <div className="col-md-4">
          <div className="card shadow-sm">
            <div className="card-body">
              <h5 className="card-title">Profile</h5>

              {/* Profile Details */}
              <div className="mb-3">
                <label className="form-label">Name</label>
                <input
                  type="text"
                  className="form-control"
                  name="name"
                  value={profileData.name}
                  onChange={handleInputChange}
                  disabled={!isEditing}
                />
              </div>

              <div className="mb-3">
                <label className="form-label">Email</label>
                <input
                  type="email"
                  className="form-control"
                  name="email"
                  value={profileData.email}
                  onChange={handleInputChange}
                  disabled={!isEditing}
                />
              </div>

              {/* Dropdown Menu */}
              <div className="dropdown">
                <button
                  className="btn btn-secondary dropdown-toggle w-100"
                  type="button"
                  id="dropdownMenuButton"
                  data-bs-toggle="dropdown"
                  aria-expanded="false"
                >
                  Profile Options
                </button>
                <ul className="dropdown-menu" aria-labelledby="dropdownMenuButton">
                  <li>
                    <button
                      className="dropdown-item"
                      onClick={handleEditProfile}
                    >
                      {isEditing ? "Save Profile" : "Edit Profile"}
                    </button>
                  </li>
                  <li>
                    <button
                      className="dropdown-item text-danger"
                      onClick={handleLogout}
                    >
                      Logout
                    </button>
                  </li>
                </ul>
              </div>
            </div>
          </div>
        </div>
      </div>

      {/* Toastify Container for displaying notifications */}
      <ToastContainer 
        position="top-right"
        autoClose={3000}
        hideProgressBar
        closeOnClick
        pauseOnHover
        draggable
        theme="colored"
      />

      {/* Edit Profile Circular Button (Top-left corner) */}
      <button
        className="btn btn-warning edit-profile-btn"
        onClick={handleEditProfile}
        style={{
          position: "fixed",
          top: "20px",
          left: "20px",
          width: "50px",
          height: "50px",
          borderRadius: "50%",
          padding: "10px",
          fontSize: "20px",
          zIndex: "1000",
        }}
      >
        {isEditing ? "✔" : "✎"}
      </button>
    </div>
  );
};

export default Shopkeeper;
