import React, { useEffect, useState } from "react";
import axios from "axios";
import Navbar from "../components/Navbar";

const MarketRates = () => {
  const [crops, setCrops] = useState([]);

  useEffect(() => {
    axios.get("http://localhost:/api/crops") // Adjust the URL as per backend setup
      .then((response) => setCrops(response.data))
      .catch((error) => console.error("Error fetching crops:", error));
  }, []);

  return (
    <div>
      <Navbar />
      <div className="card shadow-sm" style={{ maxWidth: "600px", margin: "0 auto", marginTop: "20px" }}>
        <div className="card-body">
          <h5 className="card-title text-center mb-4" style={{ color: "#333" }}>
            Market Rates (₹ per Kg)
          </h5>
          <table className="table table-bordered text-center">
            <thead className="thead-light">
              <tr>
                <th>Crop</th>
                <th>Rate per Kg (₹)</th>
              </tr>
            </thead>
            <tbody>
              {crops.length > 0 ? (
                crops.map((crop) => (
                  <tr key={crop.cropId}>
                    <td>{crop.cropName}</td>
                    <td>₹{crop.rate}/kg</td>
                  </tr>
                ))
              ) : (
                <tr>
                  <td colSpan="2">Loading...</td>
                </tr>
              )}
            </tbody>
          </table>
        </div>
      </div>
    </div>
  );
};

export default MarketRates;
