import React, { useState } from "react";
import Navbar from "../components/Navbar";
import { Link } from "react-router-dom";
import { ToastContainer, toast } from "react-toastify";
import "react-toastify/dist/ReactToastify.css";

const Shopkeeper = () => {
  const [profileData, setProfileData] = useState({
    name: "John Doe",
    email: "johndoe@example.com",
  });
  const [isEditing, setIsEditing] = useState(false);

  // Handle input change for editing profile
  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setProfileData({
      ...profileData,
      [name]: value,
    });
  };

  // Toggle between edit and save profile state
  const handleEditProfile = () => {
    if (isEditing) {
      toast.success("Profile updated successfully!");
    }
    setIsEditing(!isEditing);
  };

  const handleLogout = () => {
    toast.success("Successfully logged out!");
  };

  return (
    <div className="container">
      <Navbar />
      <div className="row mt-4">
        {/* Left Section */}
        <div className="col-md-8">
          <div className="mb-4">
            <h3>Transaction History</h3>
            <ul className="list-group">
              <li className="list-group-item">
                <Link to="/transaction-history">View All Transactions</Link>
              </li>
            </ul>
          </div>

          <div className="mb-4">
            <h3>Farmer Details</h3>
            <ul className="list-group">
              <li className="list-group-item">
                <Link to="/farmer-details">View Farmer Details</Link>
              </li>
            </ul>
          </div>

          <div className="mb-4">
            <h3>Generate Bill</h3>
            <Link to="/generate-bill" className="btn btn-primary">
              Generate Bill
            </Link>
          </div>
        </div>

        {/* Right Section (Profile Section) */}
        <div className="col-md-4">
          <div className="card shadow-sm">
            <div className="card-body">
              <h5 className="card-title">Profile</h5>

              {/* Profile Details */}
              <div className="mb-3">
                <label className="form-label">Name</label>
                <input
                  type="text"
                  className="form-control"
                  name="name"
                  value={profileData.name}
                  onChange={handleInputChange}
                  disabled={!isEditing}
                />
              </div>

              <div className="mb-3">
                <label className="form-label">Email</label>
                <input
                  type="email"
                  className="form-control"
                  name="email"
                  value={profileData.email}
                  onChange={handleInputChange}
                  disabled={!isEditing}
                />
              </div>

              {/* Dropdown Menu */}
              <div className="dropdown">
                <button
                  className="btn btn-secondary dropdown-toggle w-100"
                  type="button"
                  id="dropdownMenuButton"
                  data-bs-toggle="dropdown"
                  aria-expanded="false"
                >
                  Profile Options
                </button>
                <ul className="dropdown-menu" aria-labelledby="dropdownMenuButton">
                  <li>
                    <button className="dropdown-item" onClick={handleEditProfile}>
                      {isEditing ? "Cancel Edit" : "Edit Profile"}
                    </button>
                  </li>
                  <li>
                    <button className="dropdown-item text-danger" onClick={handleLogout}>
                      Logout
                    </button>
                  </li>
                </ul>
              </div>

              {/* Save Changes Button (Visible only when editing) */}
              {isEditing && (
                <button
                  className="btn btn-success mt-3 float-end"
                  onClick={handleEditProfile}
                >
                  Save Changes
                </button>
              )}
            </div>
          </div>
        </div>
      </div>

      {/* Toastify Container for displaying notifications */}
      <ToastContainer
        position="top-right"
        autoClose={3000}
        hideProgressBar
        closeOnClick
        pauseOnHover
        draggable
        theme="colored"
      />
    </div>
  );
};

export default Shopkeeper;
