import React, { useState, useEffect } from "react";
import { ToastContainer, toast } from "react-toastify";
import "react-toastify/dist/ReactToastify.css";

const BuyerDashboard = () => {
  const [orders, setOrders] = useState([]);
  const [payments, setPayments] = useState([]);
  const [stock, setStock] = useState([]);
  const [notifications, setNotifications] = useState([
    "New shipment of organic carrots available!",
    "Auction starting in 1 hour!",
  ]);

  // Simulate fetching data from an API
  useEffect(() => {
    setStock([
      { name: "Tomatoes", quantity: 500, price: 30, auctionTime: "2:00 PM" },
      { name: "Potatoes", quantity: 1000, price: 25, auctionTime: "3:00 PM" },
      { name: "Wheat", quantity: 800, price: 40, auctionTime: "4:00 PM" },
    ]);
    setOrders([
      {
        id: "1201",
        produce: "Tomatoes",
        quantity: 300,
        status: "Pending",
        deliveryDate: "31st Jan",
      },
      {
        id: "1202",
        produce: "Wheat",
        quantity: 400,
        status: "Delivered",
        deliveryDate: "28th Jan",
      },
    ]);
    setPayments([
      {
        invoice: "INV12345",
        amount: "₹5000",
        status: "Completed",
        date: "28th Jan",
      },
      {
        invoice: "INV12346",
        amount: "₹2000",
        status: "Pending",
        date: "29th Jan",
      },
    ]);
  }, []);

  // Toastify notification for new message
  const notify = (message) => {
    toast.info(message);
  };

  return (
    <div className="dashboard-container">
      <ToastContainer />
      <div className="header">
        <div className="logo">[Logo]</div>
        <div className="profile">[Profile / Logout]</div>
      </div>

      <div className="main-content">
        <div className="sidebar">
          <ul>
            <li>Dashboard</li>
            <li>My Orders</li>
            <li>Inventory</li>
            <li>Payments</li>
            <li>Order History</li>
            <li>Notifications</li>
          </ul>
        </div>

        <div className="dashboard-main">
          <h2>Welcome, Buyer!</h2>

          {/* Stock Overview */}
          <div className="stock-overview">
            <h3>Stock Overview</h3>
            <table>
              <thead>
                <tr>
                  <th>Produce Name</th>
                  <th>Quantity</th>
                  <th>Price (per kg)</th>
                  <th>Auction Time</th>
                </tr>
              </thead>
              <tbody>
                {stock.map((item) => (
                  <tr key={item.name}>
                    <td>{item.name}</td>
                    <td>{item.quantity} kg</td>
                    <td>{item.price}</td>
                    <td>{item.auctionTime}</td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>

          {/* Current Orders */}
          <div className="current-orders">
            <h3>Current Orders</h3>
            <table>
              <thead>
                <tr>
                  <th>Order ID</th>
                  <th>Produce</th>
                  <th>Quantity</th>
                  <th>Status</th>
                  <th>Delivery Date</th>
                </tr>
              </thead>
              <tbody>
                {orders.map((order) => (
                  <tr key={order.id}>
                    <td>{order.id}</td>
                    <td>{order.produce}</td>
                    <td>{order.quantity} kg</td>
                    <td>{order.status}</td>
                    <td>{order.deliveryDate}</td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>

          {/* Recent Payments */}
          <div className="recent-payments">
            <h3>Recent Payments</h3>
            <table>
              <thead>
                <tr>
                  <th>Invoice No</th>
                  <th>Amount Paid</th>
                  <th>Status</th>
                  <th>Date</th>
                </tr>
              </thead>
              <tbody>
                {payments.map((payment) => (
                  <tr key={payment.invoice}>
                    <td>{payment.invoice}</td>
                    <td>{payment.amount}</td>
                    <td>{payment.status}</td>
                    <td>{payment.date}</td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>

          {/* Notifications */}
          <div className="notifications">
            <h3>Notifications</h3>
            {notifications.map((notification, index) => (
              <div
                key={index}
                className="notification"
                onClick={() => notify(notification)}
              >
                {notification}
              </div>
            ))}
          </div>
        </div>
      </div>
    </div>
  );
};

export default BuyerDashboard;
