import React, { useState } from "react";
import Navbar from "../components/Navbar";
import { ToastContainer, toast } from "react-toastify"; // Import Toastify
import "react-toastify/dist/ReactToastify.css"; // Import Toastify styles

function Feedback() {
  const [feedback, setFeedback] = useState("");
  const [rating, setRating] = useState(0); // State to track the rating

  const handleSubmit = (e) => {
    e.preventDefault();

    // Check if feedback and rating are provided
    if (!feedback || rating === 0) {
      toast.error("Please provide feedback and a rating!");
      return;
    }

    console.log("Feedback submitted:", feedback);
    console.log("Rating submitted:", rating);
    
    // Show success toast when feedback is submitted
    toast.success("Thank you for your feedback!");
    
    // Clear the feedback and rating after submission
    setFeedback("");
    setRating(0);
  };

  return (
    <div className="container">
      <Navbar />
      <br />
      <br />
      <br />
      <h2>Feedback</h2>
      
      {/* Rating Section */}
      <div className="d-flex justify-content-center mb-3">
        {[1, 2, 3, 4, 5].map((star) => (
          <span
            key={star}
            onClick={() => setRating(star)}
            style={{
              fontSize: "2rem",
              color: star <= rating ? "#ffc107" : "#e4e5e9", // Gold if selected
              cursor: "pointer",
            }}
          >
            â˜…
          </span>
        ))}
      </div>

      <form onSubmit={handleSubmit}>
        <div className="mb-3">
          <textarea
            className="form-control"
            value={feedback}
            onChange={(e) => setFeedback(e.target.value)}
            placeholder="Enter your feedback"
            required
          />
        </div>
        <button type="submit" className="btn btn-primary">
          Submit
        </button>
      </form>
      
      {/* Toastify Container for displaying notifications */}
      <ToastContainer 
        position="top-right"
        autoClose={3000}
        hideProgressBar
        closeOnClick
        pauseOnHover
        draggable
        theme="colored"
      />
    </div>
  );
}

export default Feedback;
