import React, { useState } from "react";
import { ToastContainer, toast } from "react-toastify";
import "react-toastify/dist/ReactToastify.css";
import "bootstrap/dist/css/bootstrap.min.css";
import "bootstrap/dist/js/bootstrap.bundle.min.js";

const FarmerDashboard = () => {
  const [farmer, setFarmer] = useState({
    name: "John Doe",
    email: "johndoe@example.com",
    phone: "123-456-7890",
  });

  const [isEditing, setIsEditing] = useState(false);

  const handleEditProfile = (e) => {
    e.stopPropagation(); // Prevent dropdown from closing
    setIsEditing(true);
  };

  const handleLogout = () => toast.success("Logged out successfully!");

  const handleSaveProfile = (e) => {
    e.stopPropagation(); // Prevent dropdown from closing
    setIsEditing(false);
    toast.success("Profile saved successfully!");
  };

  return (
    <div className="container mt-5">
      <div className="row">
        {/* Profile Dropdown */}
        <div className="col-md-12 text-end mb-3">
          <div className="dropdown">
            <button
              className="btn btn-secondary dropdown-toggle rounded-circle p-3 d-flex align-items-center justify-content-center"
              type="button"
              id="profileDropdown"
              data-bs-toggle="dropdown"
              aria-expanded="false"
              style={{
                width: "50px",
                height: "50px",
                fontSize: "1.5rem",
                fontWeight: "bold",
              }}
            >
              {farmer.name.charAt(0)}
            </button>

            {/* Prevent dropdown from closing when clicking inside */}
            <ul className="dropdown-menu dropdown-menu-end" aria-labelledby="profileDropdown" data-bs-auto-close="outside">
              {isEditing ? (
                <li className="dropdown-item">
                  <input
                    type="text"
                    className="form-control mb-2"
                    value={farmer.name}
                    onChange={(e) => setFarmer({ ...farmer, name: e.target.value })}
                  />
                  <input
                    type="email"
                    className="form-control mb-2"
                    value={farmer.email}
                    onChange={(e) => setFarmer({ ...farmer, email: e.target.value })}
                  />
                  <input
                    type="text"
                    className="form-control mb-2"
                    value={farmer.phone}
                    onChange={(e) => setFarmer({ ...farmer, phone: e.target.value })}
                  />
                  <button className="btn btn-primary w-100" onClick={handleSaveProfile}>
                    Save Profile
                  </button>
                </li>
              ) : (
                <>
                  <li className="dropdown-item"><strong>{farmer.name}</strong></li>
                  <li className="dropdown-item">{farmer.email}</li>
                  <li className="dropdown-item">{farmer.phone}</li>
                  <li><button className="dropdown-item" onClick={handleEditProfile}>Edit Profile</button></li>
                  <li><button className="dropdown-item text-danger" onClick={handleLogout}>Logout</button></li>
                </>
              )}
            </ul>
          </div>
        </div>
      </div>

      <ToastContainer />
    </div>
  );
};

export default FarmerDashboard;
