import React from "react";
import Navbar from "../components/navbar";

function HomePage() {
  return (
    <div
      className="min-h-screen bg-cover bg-center flex flex-col items-center justify-center text-white"
      style={{
        backgroundImage: "url('/images/9061738.jpg')",
      }}
    >
      <Navbar />
      <div className="bg- bg-opacity-50 p-8 rounded-2xl shadow-lg text-center">
        <h1 className="text-4xl font-bold mb-4">Welcome to FARMER Welfare</h1>

        <p className="text-lg mb-6">
          This is the home page of the APMC Portal where farmers can access
          market rates, feedback, and more.
        </p>
        <hr className="border-gray-500 my-4" />
        <p className="text-md">
          Get started by exploring the navigation above.
        </p>
      </div>
    </div>
  );
}

export default HomePage;
