import React, { useState } from "react";
import Navbar from "../components/Navbar"; // Assuming Navbar is imported from components
import { Link } from "react-router-dom";
import { ToastContainer, toast } from "react-toastify"; // Import Toastify
import "react-toastify/dist/ReactToastify.css"; // Import Toastify styles

const Shopkeeper = () => {
  const [profileData, setProfileData] = useState({
    name: "John Doe",
    email: "johndoe@example.com",
  });

  const handleLogout = () => {
    // Handle logout logic (e.g., clearing sessionStorage or redirecting to login page)
    toast.success("Successfully logged out!");
  };

  const handleEditProfile = () => {
    // Logic to handle profile editing
    toast.info("Editing profile...");
  };

  return (
    <div className="container">
      <Navbar />
      <div className="row mt-4">
        {/* Left Section */}
        <div className="col-md-8">
          <div className="mb-4">
            <h3>Transaction History</h3>
            <ul className="list-group">
              <li className="list-group-item">
                <Link to="/transaction-history">View All Transactions</Link>
              </li>
            </ul>
          </div>

          <div className="mb-4">
            <h3>Farmer Details</h3>
            <ul className="list-group">
              <li className="list-group-item">
                <Link to="/farmer-details">View Farmer Details</Link>
              </li>
            </ul>
          </div>

          <div className="mb-4">
            <h3>Generate Bill</h3>
            <Link to="/generate-bill" className="btn btn-primary">
              Generate Bill
            </Link>
          </div>
        </div>

        {/* Right Section (Profile Section) */}
        <div className="col-md-4">
          <div className="card shadow-sm">
            <div className="card-body">
              <h5 className="card-title">Profile</h5>
              <p><strong>Name:</strong> {profileData.name}</p>
              <p><strong>Email:</strong> {profileData.email}</p>

              {/* Edit Profile and Logout buttons */}
              <button className="btn btn-warning w-100 mb-2" onClick={handleEditProfile}>
                Edit Profile
              </button>
              <button className="btn btn-danger w-100" onClick={handleLogout}>
                Logout
              </button>
            </div>
          </div>
        </div>
      </div>

      {/* Toastify Container for displaying notifications */}
      <ToastContainer 
        position="top-right"
        autoClose={3000}
        hideProgressBar
        closeOnClick
        pauseOnHover
        draggable
        theme="colored"
      />
    </div>
  );
};

export default Shopkeeper;
