import React, { useEffect, useState } from "react";
import axios from "axios";
import Navbar from "../components/Navbar";

const MarketRates = () => {
  const [crops, setCrops] = useState([]);
  const [error, setError] = useState(null);

  useEffect(() => {
    axios.get("http://localhost:7299/api/Crops")
        .then(response => {
            if (response.data.data) {
                console.log("Crops Data:", response.data.data);
                setCrops(response.data.data); // ✅ Extract crops from 'data' field
            } else {
                console.warn("No crops found:", response.data.message);
            }
        })
        .catch(error => {
            console.error("Error fetching crops:", error);
        });
}, []);



  return (
    <div>
      <Navbar />
      <div
        className="card shadow-sm"
        style={{ maxWidth: "600px", margin: "0 auto", marginTop: "20px" }}
      >
        <div className="card-body">
          <h5 className="card-title text-center mb-4" style={{ color: "#333" }}>
            Market Rates (₹ per Kg)
          </h5>
          {error ? (
            <p className="text-danger text-center">{error}</p>
          ) : (
            <table className="table table-bordered text-center">
              <thead className="thead-light">
                <tr>
                  <th>Crop</th>
                  <th>Rate per Kg (₹)</th>
                </tr>
              </thead>
              <tbody>
                {crops.length > 0 ? (
                  crops.map((crop) => (
                    <tr key={crop.cropId}>
                      <td>{crop.cropName}</td>
                      <td>₹{crop.rate}/kg</td>
                    </tr>
                  ))
                ) : (
                  <tr>
                    <td colSpan="2">Loading...</td>
                  </tr>
                )}
              </tbody>
            </table>
          )}
        </div>
      </div>
    </div>
  );
};

export default MarketRates;
