import React from "react";
import Navbar from "../components/navbar";

function HomePage() {
  return (
    <div
      className="relative min-h-screen bg-cover bg-center text-white"
      style={{
        backgroundImage:
          "url('https://source.unsplash.com/1600x900/?farm,vegetables')",
      }}
    >
      {/* Overlay */}
      <div className="absolute inset-0 bg-black bg-opacity-50"></div>

      {/* Content */}
      <div className="relative z-10 flex flex-col items-center justify-center min-h-screen p-6">
        <Navbar />

        <div className="text-center mt-20">
          <h1 className="text-5xl md:text-6xl font-bold mb-6">
            Welcome to <span className="text-green-400">FARMER Welfare</span>
          </h1>
          <p className="text-xl md:text-2xl mb-8">
            Access market rates, feedback, and more to empower farmers.
          </p>
        </div>

        {/* Market Rates Section */}
        <div className="flex items-center justify-center bg-white bg-opacity-80 rounded-2xl p-10 shadow-2xl mt-10">
          <h2 className="text-4xl md:text-6xl font-bold text-green-700">
            Tomato Market Rate: â‚¹50/kg
          </h2>
        </div>
      </div>
    </div>
  );
}

export default HomePage;
