import React, { useState, useEffect } from "react";
import axios from "axios";
import { ToastContainer, toast } from "react-toastify";
import "react-toastify/dist/ReactToastify.css";
import "bootstrap/dist/css/bootstrap.min.css";
import "bootstrap/dist/js/bootstrap.bundle.min.js";
import Navbar from "../components/Navbar"; // ✅ Import Navbar

const FarmerDashboard = () => {
  const [farmer, setFarmer] = useState(null); // ✅ Initially null
  const [isEditing, setIsEditing] = useState(false);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [crops, setCrops] = useState([]);

  useEffect(() => {
    const fetchFarmerData = async () => {
      const token = sessionStorage.getItem("token"); // ✅ Retrieve token for authentication

      const user=ses

      try {
        const response = await axios.get("http://localhost:7299/api/Farmer/Profile", {
          headers: { Authorization: `Bearer ${token}` },
        });

        setFarmer(response.data);
      } catch (err) {
        console.error("Error fetching farmer data:", err);
        setError("Failed to load profile data.");
      } finally {
        setLoading(false);
      }
    };

    const fetchCrops = async () => {
      try {
        const response = await axios.get("http://localhost:7299/api/Crops");
        setCrops(response.data.data || []); // ✅ Handle empty response
      } catch (err) {
        console.error("Error fetching crops:", err);
        setError("Failed to load market rates.");
      }
    };

    fetchFarmerData();
    fetchCrops();
  }, []);

  const handleEditProfile = () => setIsEditing(true);

  const handleSaveProfile = async () => {
    const token = localStorage.getItem("token");

    try {
      await axios.put("http://localhost:7299/api/Farmer/UpdateProfile", farmer, {
        headers: { Authorization: `Bearer ${token}` },
      });
      toast.success("Profile updated successfully!");
      setIsEditing(false);
    } catch (err) {
      console.error("Error updating profile:", err);
      toast.error("Failed to update profile.");
    }
  };

  const handleLogout = () => {
    localStorage.removeItem("token");
    toast.success("Logged out successfully!");
    setTimeout(() => (window.location.href = "/login"), 1000);
  };

  if (loading) return <p className="text-center mt-5">Loading...</p>;
  if (error) return <p className="text-danger text-center mt-5">{error}</p>;

  return (
    <div>
      <Navbar /> {/* ✅ Include Navbar */}

      <div className="container mt-5">
        <div className="row">
          {/* Profile Dropdown */}
          <div className="col-md-12 text-end mb-3">
            <div className="dropdown">
              <button
                className="btn btn-secondary dropdown-toggle rounded-circle p-3 d-flex align-items-center justify-content-center"
                type="button"
                id="profileDropdown"
                data-bs-toggle="dropdown"
                aria-expanded="false"
                style={{
                  width: "50px",
                  height: "50px",
                  fontSize: "1.5rem",
                  fontWeight: "bold",
                }}
              >
                {farmer.firstName.charAt(0)}
              </button>

              <ul className="dropdown-menu dropdown-menu-end" aria-labelledby="profileDropdown" data-bs-auto-close="outside">
                {isEditing ? (
                  <li className="dropdown-item">
                    <input
                      type="text"
                      className="form-control mb-2"
                      value={farmer.firstName}
                      onChange={(e) => setFarmer({ ...farmer, firstName: e.target.value })}
                    />
                    <input
                      type="text"
                      className="form-control mb-2"
                      value={farmer.lastName}
                      onChange={(e) => setFarmer({ ...farmer, lastName: e.target.value })}
                    />
                    <input
                      type="email"
                      className="form-control mb-2"
                      value={farmer.email}
                      onChange={(e) => setFarmer({ ...farmer, email: e.target.value })}
                    />
                    <input
                      type="text"
                      className="form-control mb-2"
                      value={farmer.contactNo}
                      onChange={(e) => setFarmer({ ...farmer, contactNo: e.target.value })}
                    />
                    <button className="btn btn-primary w-100" onClick={handleSaveProfile}>
                      Save Profile
                    </button>
                  </li>
                ) : (
                  <>
                    <li className="dropdown-item"><strong>{farmer.firstName} {farmer.lastName}</strong></li>
                    <li className="dropdown-item">{farmer.email}</li>
                    <li className="dropdown-item">{farmer.contactNo}</li>
                    <li><button className="dropdown-item" onClick={handleEditProfile}>Edit Profile</button></li>
                    <li><button className="dropdown-item text-danger" onClick={handleLogout}>Logout</button></li>
                  </>
                )}
              </ul>
            </div>
          </div>

          {/* Market Rates Table */}
          <div className="col-md-12">
            <div className="card p-4 shadow">
              <h2 className="text-center">Market Rates (₹ per Kg)</h2>
              <table className="table table-bordered mt-3">
                <thead className="table-dark">
                  <tr>
                    <th>Crop</th>
                    <th>Rate per Kg (₹)</th>
                  </tr>
                </thead>
                <tbody>
                  {crops.length > 0 ? (
                    crops.map((crop) => (
                      <tr key={crop.cropId}>
                        <td>{crop.cropName}</td>
                        <td>₹{crop.rate}/kg</td>
                      </tr>
                    ))
                  ) : (
                    <tr>
                      <td colSpan="2" className="text-center">No market rates available</td>
                    </tr>
                  )}
                </tbody>
              </table>
            </div>
          </div>

        </div>
      </div>

      <ToastContainer />
    </div>
  );
};

export default FarmerDashboard;
