import React, { useState } from "react";
import Navbar from "../components/Navbar";
import { ToastContainer, toast } from "react-toastify";
import "react-toastify/dist/ReactToastify.css";
import "bootstrap/dist/css/bootstrap.min.css";
import "bootstrap/dist/js/bootstrap.bundle.min.js";

const FarmerDashboard = () => {
  const [farmer, setFarmer] = useState({
    name: "John Doe",
    email: "johndoe@example.com",
    phone: "123-456-7890",
  });

  const [isEditing, setIsEditing] = useState(false);

  const handleEditProfile = () => setIsEditing(true);
  const handleLogout = () => toast.success("Logged out successfully!");
  const handleSaveProfile = () => {
    setIsEditing(false);
    toast.success("Profile updated successfully!");
  };

  return (
    <div className="container">
      <Navbar />
      <h2 className="mt-4 text-center">Farmer Dashboard</h2>
      <div className="card p-4 shadow mt-3">
        <h3>Profile Details</h3>
        {isEditing ? (
          <>
            <input
              type="text"
              className="form-control mb-2"
              value={farmer.name}
              onChange={(e) => setFarmer({ ...farmer, name: e.target.value })}
              placeholder="Name"
            />
            <input
              type="email"
              className="form-control mb-2"
              value={farmer.email}
              onChange={(e) => setFarmer({ ...farmer, email: e.target.value })}
              placeholder="Email"
            />
            <input
              type="text"
              className="form-control mb-2"
              value={farmer.phone}
              onChange={(e) => setFarmer({ ...farmer, phone: e.target.value })}
              placeholder="Phone"
            />
            <button className="btn btn-success" onClick={handleSaveProfile}>Save</button>
          </>
        ) : (
          <>
            <p><strong>Name:</strong> {farmer.name}</p>
            <p><strong>Email:</strong> {farmer.email}</p>
            <p><strong>Phone:</strong> {farmer.phone}</p>
            <button className="btn btn-primary me-2" onClick={handleEditProfile}>Edit Profile</button>
            <button className="btn btn-danger" onClick={handleLogout}>Logout</button>
          </>
        )}
      </div>
      <ToastContainer />
    </div>
  );
};

export default FarmerDashboard;
