import React, { useState, useEffect } from "react";
import Navbar from "../components/Navbar";
import { ToastContainer, toast } from "react-toastify";
import "react-toastify/dist/ReactToastify.css";
import "bootstrap/dist/css/bootstrap.min.css";
import "bootstrap/dist/js/bootstrap.bundle.min.js";
import { getBuyerProfile, updateBuyerProfile } from "../service/BuyerService"; // ✅ Import API calls

const BuyerDetails = () => {
  const [buyer, setBuyer] = useState({
    userID: "",
    firstName: "",
    lastName: "",
    contactNo: "",
    email: "",
    role: "Buyer",
  });

  const [isEditing, setIsEditing] = useState(false);

  useEffect(() => {
    fetchBuyerProfile(); // ✅ Fetch buyer profile from DB
  }, []);

  const fetchBuyerProfile = async () => {
    try {
      const response = await getBuyerProfile();
      setBuyer(response); // ✅ Set buyer details from API response
    } catch (error) {
      toast.error("Failed to load profile.");
    }
  };

  const handleEditProfile = () => setIsEditing(true);

  const handleSaveProfile = async () => {
    try {
      await updateBuyerProfile(buyer);
      setIsEditing(false);
      sessionStorage.setItem("user", JSON.stringify(buyer)); // ✅ Update session storage
      toast.success("Profile updated successfully!");
    } catch (error) {
      toast.error("Failed to update profile.");
    }
  };

  return (
    <div className="container">
      <Navbar />
      <h2 className="mt-4 text-center">Buyer Dashboard</h2>
      <div className="card p-4 shadow mt-3">
        <h3>Profile Details</h3>
        {isEditing ? (
          <>
            <input
              type="text"
              className="form-control mb-2"
              value={buyer.firstName}
              readOnly // Prevent name change
            />
            <input
              type="text"
              className="form-control mb-2"
              value={buyer.lastName}
              readOnly // Prevent name change
            />
            <input
              type="email"
              className="form-control mb-2"
              value={buyer.email}
              onChange={(e) => setBuyer({ ...buyer, email: e.target.value })}
              placeholder="Email"
            />
            <input
              type="text"
              className="form-control mb-2"
              value={buyer.contactNo}
              onChange={(e) => setBuyer({ ...buyer, contactNo: e.target.value })}
              placeholder="Contact Number"
            />
            <button className="btn btn-success" onClick={handleSaveProfile}>
              Save
            </button>
          </>
        ) : (
          <>
            <p><strong>Name:</strong> {buyer.firstName} {buyer.lastName}</p>
            <p><strong>Email:</strong> {buyer.email}</p>
            <p><strong>Contact No:</strong> {buyer.contactNo}</p>
            <button className="btn btn-primary" onClick={handleEditProfile}>
              Edit Profile
            </button>
          </>
        )}
      </div>
      <ToastContainer />
    </div>
  );
};

export default BuyerDetails;
